	OPEN tech_cursor;
    tech_loop: LOOP
		FETCH tech_cursor INTO curr_tech_playerid, curr_tech_key, curr_tech_id;
        IF (techs_done) THEN
			LEAVE tech_loop;
		END IF;
        IF (curr_tech_key=-1) THEN
		ELSEIF (curr_tech_key=0) THEN
		END IF;
	END LOOP;
    CLOSE tech_cursor;
    
    IF EXISTS (SELECT * FROM BB_INFO WHERE USER_ID = p_identifier) THEN
		UPDATE BB_INFO SET GAME_ID=v_game_id, OPPONENT_1 = v_opp1, OPPONENT_2 = v_opp2, OPPONENT_3 = v_opp3, OPPONENT_4 = v_opp4, OPPONENT_5 = v_opp5, OPPONENT_6 = v_opp6, OPPONENT_7 = v_opp7 WHERE USER_ID=p_identifier;
	ELSE
	END IF;
 RETURN (v_game_id);
END;

